import React from 'react';
import { Link } from '@reach/router';
import ArticleVoteButton from './ArticleVoteButton';
import '../App.css';

const ArticleCard = (props) => {
	let articleArray = [];

	if (props.article_id) {
		articleArray = [ props.articles_list ];
		return articleArray.map((article) => {
			return (
				<ul key={article.article_id}>
					<li id="article_card">
						<h2>{article.title}</h2>
						<br />
						<br />
						Author: {article.author}
						<br />
						Created at: {article.created_at}
						<br />
						<p>{article.body}</p>
						<ArticleVoteButton votes={props.votes} article_id={props.article_id} />
					</li>
				</ul>
			);
		});
	} else {
		articleArray = props.articles_list;

		return articleArray.map((article) => {
			return (
				<Link id="sort_by" to={`/${article.article_id}`}>
					<label key={article.article_id}>
						<li id="card">
							<span id="article_heading">{article.title}</span>
							<br />
							Created at:{article.created_at}, Comments: {article.comment_count}, Votes:
							{article.votes}
						</li>
						<br />
					</label>
				</Link>
			);
		});
	}
};

export default ArticleCard;
