import axios from 'axios';

export const getArticles = (topic, sort_by) => {
	console.log(topic, sort_by);
	return axios
		.get('http://callum-goodley-nc-news-app.herokuapp.com/api/articles', {
			params: {
				topic: topic,
				sort_by: sort_by
			}
		})
		.then((articles) => {
			return articles.data.articles;
		});
};
