import React from 'react';
import { incrementVotes } from '../API';

const VoteButton = (props) => {
	const handleClick = (incrementBy) => {
		const { votes, comment_id } = props;
		incrementVotes(votes, comment_id, incrementBy);
	};

	return (
		<main>
			<button key="vote up button" name="vote up" value={props.comment_id} onClick={props.handleClick(-1)}>
				vote ↓
			</button>
			<button key="vote down button" name="vote down" value={props.comment_id} onClick={props.handleClick(-1)}>
				vote ↓
			</button>
		</main>
	);
};

export default VoteButton;
