import React from 'react';
import { incrementVotes } from '../API';

class VoteButton extends React.Component {
	state = {
		voteChange: 0
	};

	render() {
		return (
			<main>
				<button key="vote up button" name={1} value={this.props.comment_id} onClick={this.handleClick}>
					vote ↑
				</button>
				<p>votes: {this.props.votes + this.state.voteChange}</p>
				<button key="vote down button" name={-1} value={this.props.comment_id} onClick={this.handleClick}>
					vote ↓
				</button>
			</main>
		);
	}
	handleClick = (event) => {
		const increment_by = event.target.name;
		const comment_id = event.target.value;
		incrementVotes(comment_id, increment_by);
		this.setState((currentState) => {
			return { voteChange: currentState.voteChange + increment_by };
		});
	};
}

export default VoteButton;
