import axios from 'axios';

export const getArticles = ({ topic }) => {
	return axios
		.get('http://callum-goodley-nc-news-app.herokuapp.com/api/articles', {
			params: {
				topic: topic
			}
		})
		.then((articles) => {
			return articles.data.articles;
		});
};
