import axios from 'axios';

export const getArticles = (props) => {
	console.log(props);
	return axios
		.get('http://callum-goodley-nc-news-app.herokuapp.com/api/articles', {
			params: {
				// topic: props.topic
			}
		})
		.then((articles) => {
			return articles.data.articles;
		});
};
