import React from 'react';
import VoteUpButton from './VoteUp';
import VoteDownButton from './VoteDown';

const CommentCard = (props) => {
	return props.comments_list.map((comment) => {
		return (
			<ul key={comment.comment_id}>
				<li key={comment.comment_id}>
					User: {comment.author}
					<br />
					<br />
					Comment: {comment.body}
					<br />
					<br />
					Votes: {comment.votes} Created at: {comment.created_at}
					<br />
					<br />
					{props.userName === comment.author ? (
						<button key={comment.comment_id} value={comment.comment_id} onClick={props.removeComment}>
							Delete comment.
						</button>
					) : null}
					<br />
					<br />
				</li>
				<VoteUpButton comment_id={comment.comment_id} voteUp={props.voteUp} />
				<VoteDownButton comment_id={comment.comment_id} voteDown={props.voteDown} />
			</ul>
		);
	});
};

export default CommentCard;
