import React from 'react';
import { getArticles } from '../API';

class ArticlesList extends React.Component {
	state = {
		articles_list: []
	};

	render() {
		return this.state.articles_list.map((article) => {
			return <li key={article.article_id}>{article.title}</li>;
		});
	}
	componentDidMount() {
		this.fetchArticles();
	}

	fetchArticles = () => {
		getArticles(this.props.topic).then((articles) => {
			this.setState({ articles_list: articles });
		});
	};
}

export default ArticlesList;
