import React from 'react';
import { Link } from '@reach/router';

const SortByInTopics = (props) => {
	let { topic } = props;
	return (
		<nav id="sort_by">
			<Link to={`/${topic}/sort/created_at`}>{`${topic}'s latest.`}</Link>
			<Link to={`/${topic}/sort/votes`}> {`${topic}'s top voted.`}</Link>
			<Link to={`/${topic}/sort/comment_count`}>{`${topic}'s most commented.`}</Link>
		</nav>
	);
};
export default SortByInTopics;
