import React from 'react';
import { incrementVotes } from '../API';

class VoteButton extends React.Component {
	state = {
		voteChange: 0
	};

	render() {
		return (
			<main>
				<button key="vote up button" name="vote up" value={this.props.comment_id} onClick={this.handleClick(1)}>
					vote ↓
				</button>
				<p>votes: {this.props.votes}</p>
				<button
					key="vote down button"
					name="vote down"
					value={this.props.comment_id}
					onClick={this.handleClick(-1)}
				>
					vote ↓
				</button>
			</main>
		);
	}
	handleClick = (increment_by) => {
		const { votes, comment_id } = this.props;
		incrementVotes(votes, comment_id, increment_by);
	};
}

export default VoteButton;
