import React from 'react';
import VoteUpButton from './VoteUp';
import VoteDownButton from './VoteDown';
import { incrementVotes } from '../API';

const CommentsCard = (props) => {
	return props.comments_list.map((comment) => {
		return (
			<ul key={comment.comment_id}>
				<li key={comment.comment_id}>
					User: {comment.author}
					<br />
					<br />
					Comment: {comment.body}
					<br />
					<br />
					Votes: {comment.votes} Created at: {comment.created_at}
					<br />
					<br />
					{this.state.userName === comment.author ? (
						<button key={comment.comment_id} value={comment.comment_id} onClick={this.removeComment}>
							Delete comment.
						</button>
					) : null}
					<br />
					<br />
				</li>
				<VoteUpButton comment_id={comment.comment_id} voteUp={this.voteUp} />
				<VoteDownButton comment_id={comment.comment_id} voteDown={this.voteDown} />
			</ul>
		);
	});
};
voteUp = (event) => {
	const incrementDirection = event.target.name;
	const comment_id = event.target.value;
	incrementVotes(incrementDirection, comment_id);
};

voteDown = (event) => {
	const incrementDirection = event.target.name;
	const comment_id = event.target.value;
	incrementVotes(incrementDirection, comment_id);
};

export default CommentsCard;
