import React from 'react';

class CommentBox extends React.Component {
	state = {
		inputValue: []
	};
	render() {
		return (
			<form>
				<label>
					<input type="text" key={this.state.inputValue} name="comment_input" onChange={this.handleChange} />
					<button>Submit comment.</button>
				</label>
			</form>
		);
	}
	handleChange = (event) => {
		console.log(event.target.value);
	};
}

export default CommentBox;
